#ifndef ALGEBRA_PAIRING_BLS128_BASIC_POLICY_HPP
#define ALGEBRA_PAIRING_BLS128_BASIC_POLICY_HPP

#include <nil/algebra/curves/bls12.hpp>

namespace nil {
    namespace algebra {
        namespace pairing {
            namespace detail {

                using namespace nil::algebra;

                template<std::size_t ModulusBits = 381, std::size_t GeneratorBits = CHAR_BIT>
                struct bls12_basic_policy;

                template<>
                struct bls12_basic_policy<381, CHAR_BIT> {

                    using number_type = curves::bls12<381, CHAR_BIT>::number_type;

                    constexpr static const typename number_type ate_loop_count =
                        number_type(0xD201000000010000_cppui64);
                    constexpr static const bool ate_is_loop_count_neg = true;
                    constexpr static const typename number_type final_exponent = number_type(
                        _cppui4314);

                    constexpr static const typename number_type final_exponent_z =
                        number_type(0xD201000000010000_cppui64);
                    constexpr static const typename number_type final_exponent_is_z_neg = true;
                };

                template<>
                struct bls12_basic_policy<377, CHAR_BIT> {

                    using number_type = bls12<377, GeneratorBits>::number_type;

                    constexpr static const typename number_type ate_loop_count =
                        number_type(0x8508C00000000001_cppui64);
                    constexpr static const bool ate_is_loop_count_neg = false;
                    constexpr static const typename number_type final_exponent = number_type(
                        _cppui4269);

                    constexpr static const typename number_type final_exponent_z =
                        number_type(0x8508C00000000001_cppui64);
                    constexpr static const typename number_type final_exponent_is_z_neg = false;
                };

            }    // namespace detail
        }        // namespace pairing
    }            // namespace algebra
}    // namespace nil

#endif    // ALGEBRA_PAIRING_BLS128_BASIC_POLICY_HPP
